<?php/*Template Name: Расписание*/?>    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/slim-select/1.26.0/slimselect.min.css"><?php/* Get header */get_header();wp_enqueue_style( 'nmc-schedule-css' );wp_enqueue_style( 'nmc-slim' );wp_enqueue_script( 'nmc-slim-js' );wp_enqueue_script( 'nmc-schedule-js' );?>    <!-- Breadcrumbs -->    <div class="container breadcrumbs-on-page">		<?php echo do_shortcode( '[flexy_breadcrumb]' ); ?>        <div class="section-title mt-3"><?php _e('Расписание приёма специалистов', 'nmc-theme') ?></div>    </div>    <!-- Content -->    <div class="container">		<?php		if ( have_posts() ) :			while ( have_posts() ) : the_post();				the_content();			endwhile;		endif;		if ( strtolower( constant( 'ICL_LANGUAGE_CODE' ) ) == 'uk' ) {			$field_key = 'field_6066e95457efb';		} else if ( strtolower( constant( 'ICL_LANGUAGE_CODE' ) ) == 'ru' ) {			$field_key = 'field_5f60d87f172e1';		}		$options = get_field_object( $field_key );		$choices = $options['choices'];		if ( $choices ) {			?>            <select id='type-select' data-url='<?php echo admin_url( 'admin-ajax.php' ); ?>'>                <option data-placeholder="true"><?php _e('Выберите направление', 'nmc-theme') ?></option>				<?php				foreach ( $choices as $value => $label ) {					?>                    <option value='<?php echo $label; ?>'><?php echo $label ?></option>					<?php				}				?>            </select>			<?php		}		?>        <div class="content" id="content">        </div>    </div><?php/* Get footer */get_footer();?>