<?php/*Template Name: Страница услугиTemplate Post Type: page, service*//* Get header */get_header();wp_enqueue_style( 'nmc-services-css' );?>    <!-- Hero -->    <div class="container-fluid single-service-hero">        <div class="block-background" style='background-image: url("<?php echo get_the_post_thumbnail_url() ?>")'></div>        <div class="container">            <div class="container px-0">				<?php				global $post;				$home_url     = get_home_url();				$services_url = get_page_link( 9 );				?>                <p class='breadcrumbs'><a href="<?php echo $home_url ?>"><?php _e( 'ГЛАВНАЯ', 'nmc-theme' ) ?></a>/<a                            href="<?php echo $services_url ?>"><?php _e( 'УСЛУГИ', 'nmc-theme' ) ?></a>/<a                            href="<?php echo $post->guid; ?>"                            class='last'><?php echo $post->post_title; ?></a>                </p>            </div>            <h1 class="single-service-title"><?php the_title(); ?></h1>        </div>    </div>    <!-- the content -->    <div class="container single-service-content">        <div class="single-service-content-inner">			<?php			if ( have_posts() ) :				while ( have_posts() ) : the_post();					the_content();				endwhile;			endif;			?>        </div>        <div class="single-service-specialist-wrapper">			<?php			$specialist_object    = get_field( 'service_specialist', $post->ID );			$id                   = $specialist_object->ID;			$name                 = $specialist_object->post_title;			$text_under_name      = get_field( 'specialist_text_under_name', $id );			$specialty            = get_field( 'specialist_specialty', $id );			$first_consultation   = get_field( 'first_consultation_price', $id );			$disable_appointments = get_field( 'disable_appointments', $id );			?>            <div class="single-service-specialist">                <a href="<?php echo get_the_permalink( $id ) ?>">                    <img class="specialist-photo" src="<?php echo get_the_post_thumbnail_url( $id ) ?>"                         alt="<?php echo $name ?>">                    <h2 class="specialist-name"><?php echo $name; ?></h2>                </a>                <p class="specialist-specialty"><?php echo $specialty ?></p>                <p class="specialist-info"><?php echo $text_under_name ?></p>                <h4 class="specialist-price"><?php echo __( 'Стоимость консультации', 'nmc-theme' ) . ' ' . $first_consultation ?></h4>				<?php				$work_days = get_field( 'work_days', $id );				if ( $work_days ) {					$no_days = [];					foreach ( $work_days as $day ) {						$eng_day = '';						switch ( $day ) {							case 'понедельник':								$eng_day = 'monday';								array_push( $no_days, 1 );								break;							case 'вторник':								$eng_day = 'tuesday';								array_push( $no_days, 2 );								break;							case 'среда':								$eng_day = 'wednesday';								array_push( $no_days, 3 );								break;							case 'четверг':								$eng_day = 'thursday';								array_push( $no_days, 4 );								break;							case 'пятница':								$eng_day = 'friday';								array_push( $no_days, 5 );								break;							case 'суббота':								$eng_day = 'saturday';								array_push( $no_days, 6 );								break;							case 'воскресенье':								$eng_day = 'sunday';								array_push( $no_days, 0 );								break;						}					}					$json = json_encode( $no_days );				} else {					$json = '';				}				if ( ! $disable_appointments ) {					?>                    <button class="button button-blue specialist-button" data-toggle="modal" data-target="#speciliastModal"                            id='specialist-button' data-daystoenable="<?php echo $json; ?>"                            data-specialist='<?php echo $name; ?>'><?php _e( 'Записаться на приём', 'nmc-theme' ) ?>                    </button>					<?php				}				?>            </div>        </div>    </div>    <!-- Back to list  -->    <div class="container mb-5 mt-5">        <a href="<?php echo get_page_link( 9 ) ?>" class='back-to-list'><?php _e( 'назад к списку', 'nmc-theme' ) ?></a>    </div>    <!-- Contacts block -->    <div class="container-fluid single-service-contacts">        <h2 class='section-title mx-auto'><?php _e( 'Записаться на прием к врачу можно двумя способами', 'nmc-theme' ) ?></h2>        <div class="row-wrapper mx-auto">            <div class="row">                <div class="col-12 col-md-6 d-flex flex-column align-items-center border-right">                    <img src="<?php echo get_template_directory_uri() . '/inc/icons/form.svg' ?>" alt="форма">                    <p><?php _e( 'Заполните форму и вам сразу перезвонит администратор (рабочее время с 9:00 до 19:00)', 'nmc-theme' ) ?></p>                    <p class="form-button" data-toggle="modal"                       data-target="#modal"><?php _e( 'Заполнить Форму', 'nmc-theme' ) ?></p>                </div>                <div class="col-12 col-md-6 d-flex flex-column align-items-center">                    <img src="<?php echo get_template_directory_uri() . '/inc/icons/phone-white.svg' ?>" alt="телефон">                    <p><?php _e( 'Позвоните администратору напрямую в сall-центр по указанным номерам телефона', 'nmc-theme' ) ?>                        :</p>                    <p class='phones'>+380 (057) 760-33-03,<br> 097-370-33-03,<br> 099-730-33-03</p>                </div>            </div>        </div>    </div><?php/* Get footer */wp_enqueue_script( 'nmc-specialist' );get_footer();?>